sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    // Abort update if a trainer modifies fields other than 'trainer'
    if (gs.hasRole('x_1521034_gym_mana.trainer') && !gs.hasRole('x_1521034_gym_mana.manager')) {
        if (current.trainer != previous.trainer) {
            return; // Allow update if only 'trainer' field changes
        }

        // List of restricted fields
        var restrictedFields = ['short_description', 'workouts', 'estimated_completion'];
        for (var i = 0; i < restrictedFields.length; i++) {
            if (current[restrictedFields[i]] != previous[restrictedFields[i]]) {
                gs.addErrorMessage('You are only allowed to update the Trainer field.');
                current.setAbortAction(true); // Abort the save
                return;
            }
        }
    }
})(current, previous);
