sys_script.script:
(function executeSecurity(record, operation, role) {
    // If the user is a trainer but not a manager
    if (gs.hasRole('x_1521034_gym_mana.trainer') && !gs.hasRole('x_1521034_gym_mana.manager')) {
        // Allow only the 'trainer' field to be editable
        record.short_description.readonly = true;
        record.workouts.readonly = true;
        record.estimated_completion.readonly = true;

        // Ensure the trainer field is writable
        record.trainer.readonly = false;
    } else if (gs.hasRole('x_1521034_gym_mana.manager')) {
        // Managers can edit all fields, no restrictions
        record.short_description.readonly = false;
        record.workouts.readonly = false;
        record.estimated_completion.readonly = false;
        record.trainer.readonly = false;
    }
    return true;
})(current, operation, gs.getUserID().toString());
