sys_script_include.script:
var createTransactionRecord = Class.create();
createTransactionRecord.prototype = {
    initialize: function() {},

	createTransaction: function(workoutPlanId, userId) {
        var transaction = new GlideRecord('x_1521034_gym_mana_transactions'); 
        transaction.initialize();
        transaction.x_1521034_gym_mana.member = userId; 
        transaction.x_1521034_gym_mana_workout_plans = workoutPlanId; 
        transaction.state = 'pending_approval';
        transaction.insert();
	},

    type: 'createTransactionRecord'
};
