sys_security_acl.script:
(function executeRule(current, operation) {
    // Allow write access if the user has the manager role
    if (gs.hasRole('x_1521034_gym_mana.manager')) {
        return true;
    }

    // Deny write access for trainers who are not managers
    if (gs.hasRole('x_1521034_gym_mana.trainer')) {
        return false; // Block edit access for trainers
    }

    return false; // Block access for other roles
})(current, operation);
